    transition: all 0.5s;

    &:hover {
        background: #201e40;
    }

    &:focus {
      outline: 0; // Better check accessibility

        /* The value fade-ins that appear */
        &::after {
            animation: zoom 1s;
            animation-iteration-count: 1;
            animation-fill-mode: both; // Fix Firefox from firing animations only once
            content: attr(data-num);
            cursor: default;
            font-size: 100px;
            position: absolute;
                top: 1.5em;
                left: 50%;
            text-align: center;
            margin-left: -24px;
            opacity: 0;
            width: 48px;    
        }
    }
}

  /* Same as above, modified for operators */
.ops:focus::after {
    content: attr(data-ops);
    margin-left: -210px;
    width: 420px;
}

  /* Same as above, modified for result */
.equals:focus::after {
    content: attr(data-result);
    margin-left: -300px;
    width: 600px;
}

  /* Reset button */

.reset {
    background: rgba(201,120,116,.28);
    color:#c97874;
    font-weight: 400;
    margin-left: -77px;
    padding: 0.5em 1em;
    position: absolute;
        top: -20em;
        left: 50%;
    width: auto;
    height: auto;

    &:hover {
        background: #c97874;
        color: #100a1c;    
    }

    /* When button is revealed */
    &.show {
        top: 20em;
        animation: fadein 4s;
    }
}

  /* Animations */

  /* Values that appear onclick */
@keyframes zoom {
    0% { 
        transform: scale(.2); 
        opacity: 1;
    }

    70% { 
        transform: scale(1); 
    }

    100% { 
        opacity: 0;
    }
}

  /* Division by zero animation */
@keyframes broken {
    0% {
        transform: translate3d(0,0,0);
        opacity: 1;
    }

    5% {
        transform: rotate(5deg);
    }

    15% {
        transform: rotate(-5deg);
    }

    20% {
        transform: rotate(5deg);
    }

    25% {
        transform: rotate(-5deg);
    }

    50% {
        transform: rotate(45deg);
    }

    70% {
        transform: translate3d(0,2000px,0);
        opacity: 1;
    }

    75% {
        opacity: 0;
    }

    100% {
        transform: translate3d(0,-2000px,0);
    }
}

  /* Reset button fadein */
@keyframes fadein {
    0% {
        top: 20em;
        opacity: 0;
    }

    50% {
        opacity: 0;
    }

    100% {
        opacity: 1;
    }
}

