    z-index: 10;
    transition: 0.5s all cubic-bezier(0, 0.92, 1, 1);
    background-color: #fff;
    position: absolute;
    width: 300px;
    top: 0;
    bottom: 0;
    right: 0px;
    min-height: 100vh;
    visibility: hidden;
