    text-decoration: none;
    text-transform: uppercase;
    font-weight: bold;
    font-size: 14px;
    background-color: #fff;
    color: #000;
}

.aside header .toggle-sidebar:after,
.aside header .toggle-sidebar:before {
    content: '';
    display: block;
    position: absolute;
    left: 20px;
    top: 50%;
    height: 3px;
    background-color: #000;
}

.aside header .toggle-sidebar:after {
    top: 30%;
    width: 30px;
}

.aside header a.toggle-sidebar:before {

    top: 70%;
    width: 20px;
}

